import { gql, useMutation } from '@apollo/client'
import React, { FC, useState } from 'react'
import { useLocalStorage, useSessionStorage } from '../../hooks';
import "../css/Login.css"
import { TextField } from '../input';
import jwt_decode from "jwt-decode"


export interface ILoginProps {
  email?: string,
  password?: string
}

// Generated by https://quicktype.io

export interface LoginOutput {
  loginUser: LoginUser;
}

export interface LoginUser {
  message: string;
  token: string;
  __typename: string;
  email: string;
  password: string
}


export const LoginField: FC<ILoginProps> = ({
  email,
  password
}: ILoginProps) => {

  const [loginUser, { data, loading, error }] = useMutation<LoginOutput>(gql`
  mutation LoginUser($loginInput: LoginInput) {
    loginUser(loginInput: $loginInput) {
      message
      token
      email
      # password
    }
  }
`)

const [token, setToken] = useSessionStorage("token", "");





  const LoginState = () => {
    if (loading) {
      return <div>loading</div>
    }

    if (error) {
      return <div>{error.message}</div>
    }

    if (!data) {
      return <div></div>
    }

    return <div>
      {data.loginUser.message}
    </div>
  }


  const [emailValue, setEmailValue] = useState("")
  const [passwordValue, setPasswordValue] = useState("")

  
  
  if(token.length > 1) {
    const decodedToken = jwt_decode(token) as any
    console.log(decodedToken);
    return (
      <div>
        {decodedToken.email}
      </div>
    )
  }

  return (
    <div>
      <div className="logged-in-user">

      </div>
      <div className="login-field">
        <TextField label='email' placeholder='Email...' onChange={(nv) => setEmailValue(nv)} />
        <TextField type='password' label='password' placeholder='Password...' onChange={(nv) => setPasswordValue(nv)}/> {/* need to hide pw here */}
        <button onClick={() => {
          loginUser({
            variables: {
              loginInput: {
                email: emailValue,
                password: passwordValue
              }
            }
          }).then((data) =>  {
            setToken(data.data?.loginUser.token || "")
          }) 
        }
        }>Log In</button>
        {LoginState()}
      </div>
    </div>
  )
}
